    private void ok_aj_prActionPerformed(java.awt.event.ActionEvent evt) {//GEN-FIRST:event_ok_aj_prActionPerformed
        int CNI=Integer.parseInt(cni.getText());
        String nom_p=n_pr.getText();
        String prenom_p=p_pr.getText();
        
        Professeur p=new Professeur();
        if(evt.getSource()==ok_aj_pr){
            p.ajoute_p(CNI, nom_p, prenom_p);
        }
        cni.setText(" ");
        
        n_pr.setText(" ");
        p_pr.setText(" ");
       
    }//GEN-LAST:event_ok_aj_prActionPerformed


